package com.nxt.maven.guaguaheadlines.utils;import android.content.Context;import android.net.ConnectivityManager;import android.net.NetworkInfo;/** * Created by Jan Maven on 2017/8/14. * Email:cyjiang_11@163.com * Description: 网络的工具类 */public class NetWorkUtils {    public static boolean isNetworkAvailable(Context context) {        if(context !=null){            ConnectivityManager cm = (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);            NetworkInfo info = cm.getActiveNetworkInfo();            if(info !=null){                return info.isAvailable();            }        }        return false;    }}